PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE user (id integer primary key, username text not null unique, email text not null unique, short_description text);
PRAGMA writable_schema=ON;
CREATE TABLE IF NOT EXISTS 'user_fts_data'(id INTEGER PRIMARY KEY, block BLOB);
CREATE TABLE IF NOT EXISTS 'user_fts_idx'(segid, term, pgno, PRIMARY KEY(segid, term)) WITHOUT ROWID;
CREATE TABLE IF NOT EXISTS 'user_fts_docsize'(id INTEGER PRIMARY KEY, sz BLOB);
CREATE TABLE IF NOT EXISTS 'user_fts_config'(k PRIMARY KEY, v) WITHOUT ROWID;
CREATE TRIGGER user_ai after insert on user begin insert into user_fts (rowid,username,short_description) values (new.id,new.username,new.short_description);end;
CREATE TRIGGER user_ad after delete on user begin insert into user_fts (user_fts,rowid,username,short_description) values ('delete',old.id,old.username,old.short_description);
end;
CREATE TRIGGER user_au after update on user begin insert into user_fts (user_fts,rowid,username,short_description) values ('delete',old.id,old.username,old.short_description);insert into user_fts (rowid,username,short_description) values (new.id,new.username,new.short_description);end;
PRAGMA writable_schema=OFF;
COMMIT;
